import React, { useState, useEffect } from 'react';
import { resumeAPI } from './resumeService';
import './App.css';
import Dashboard from './pages/Dashboard/Dashboard';

// In your routes
<Route path="/dashboard" element={<Dashboard />} />

function App() {
  const [resumes, setResumes] = useState([]);
  const [name, setName] = useState('');

  // Load resumes when page loads
  useEffect(() => {
    loadResumes();
  }, []);

  const loadResumes = async () => {
    try {
      const response = await resumeAPI.getResumes();
      setResumes(response.data);
      console.log('Resumes loaded:', response.data);
    } catch (error) {
      console.error('Error loading resumes:', error);
    }
  };

  const createResume = async () => {
    try {
      const resumeData = {
        full_name: name,
        email: `${name}@example.com`,
        phone: "123-456-7890",
        address: "123 Main Street",
        summary: "Experienced developer",
        skills: ["JavaScript", "React", "Python"],
        education: [],
        experience: [],
        projects: []
      };

      await resumeAPI.createResume(resumeData);
      setName('');
      loadResumes(); // Refresh the list
      alert('Resume created successfully!');
    } catch (error) {
      console.error('Error creating resume:', error);
      alert('Error creating resume');
    }
  };

  return (
    <div className="App" style={{ padding: '20px' }}>
      <h1>AI Powered Resume Builder ðŸš€</h1>
      <p>Backend connected successfully! âœ…</p>
      
      {/* Simple form */}
      <div style={{ margin: '20px 0' }}>
        <input 
          type="text" 
          placeholder="Enter your name" 
          value={name}
          onChange={(e) => setName(e.target.value)}
          style={{ padding: '10px', margin: '10px', width: '200px' }}
        />
        <button 
          onClick={createResume} 
          style={{ padding: '10px 20px', backgroundColor: '#4CAF50', color: 'white', border: 'none' }}
        >
          Create Resume
        </button>
      </div>

      {/* Show resumes */}
      <div>
        <h2>Your Resumes ({resumes.length})</h2>
        {resumes.length === 0 ? (
          <p>No resumes yet. Create one above!</p>
        ) : (
          resumes.map((resume, index) => (
            <div key={index} style={{ border: '1px solid #ddd', padding: '15px', margin: '10px 0', borderRadius: '5px' }}>
              <h3>{resume.full_name}</h3>
              <p><strong>Email:</strong> {resume.email}</p>
              <p><strong>Skills:</strong> {resume.skills?.join(', ')}</p>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default App;
