import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { resumeAPI } from '../../resumeService';
import html2pdf from 'html2pdf.js';
import './Dashboard.css';

const Dashboard = () => {
  const [backendStatus, setBackendStatus] = useState('checking');
  const [resumesCount, setResumesCount] = useState(0);
  const [resumes, setResumes] = useState([]);

  // Test backend connection
  useEffect(() => {
    const loadResumes = async () => {
      try {
        const response = await resumeAPI.getResumes();
        setResumesCount(response.data.length);
        setResumes(response.data);
        setBackendStatus('connected');
      } catch (error) {
        setBackendStatus('disconnected');
      }
    };
    loadResumes();
  }, []);

  const createSampleResume = async () => {
    try {
      const newResume = {
        full_name: "Demo User",
        email: "demo@example.com",
        phone: "123-456-7890",
        address: "123 Main Street",
        summary: "Professional resume created with AI assistance",
        skills: ["JavaScript", "React", "Python", "AI/ML"],
        education: [],
        experience: [],
        projects: []
      };

      await resumeAPI.createResume(newResume);

      // Refresh
      const response = await resumeAPI.getResumes();
      setResumesCount(response.data.length);
      setResumes(response.data);

    } catch (error) {
      alert('Error creating resume: ' + error.message);
    }
  };

  const handleDeleteResume = async (resumeId) => {
    if (window.confirm('Are you sure you want to delete this resume?')) {
      try {
        await resumeAPI.deleteResume(resumeId);

        // Refresh
        const response = await resumeAPI.getResumes();
        setResumesCount(response.data.length);
        setResumes(response.data);

        alert('Resume deleted successfully!');
      } catch (error) {
        alert('Error deleting resume: ' + error.message);
      }
    }
  };

  const handleDownloadPDF = (resume) => {
    // Create a temporary div to render the resume for PDF generation
    const tempDiv = document.createElement('div');
    tempDiv.innerHTML = `
      <div style="font-family: Arial, sans-serif; max-width: 800px; margin: 0 auto; padding: 20px;">
        <h1 style="color: #2563eb; border-bottom: 2px solid #2563eb; padding-bottom: 10px;">${resume.full_name}</h1>
        <div style="margin: 20px 0;">
          <p><strong>Email:</strong> ${resume.email}</p>
          <p><strong>Phone:</strong> ${resume.phone}</p>
          <p><strong>Address:</strong> ${resume.address}</p>
        </div>
        ${resume.summary ? `<div style="margin: 20px 0;"><h2 style="color: #2563eb;">Professional Summary</h2><p>${resume.summary}</p></div>` : ''}
        ${resume.skills && resume.skills.length > 0 ? `<div style="margin: 20px 0;"><h2 style="color: #2563eb;">Skills</h2><p>${resume.skills.join(', ')}</p></div>` : ''}
        ${resume.experience && resume.experience.length > 0 ? `<div style="margin: 20px 0;"><h2 style="color: #2563eb;">Experience</h2>${resume.experience.map(exp => `<div style="margin: 10px 0;"><h3>${exp.title || 'Position'}</h3><p>${exp.company || 'Company'} - ${exp.description || 'Description'}</p></div>`).join('')}</div>` : ''}
        ${resume.education && resume.education.length > 0 ? `<div style="margin: 20px 0;"><h2 style="color: #2563eb;">Education</h2>${resume.education.map(edu => `<div style="margin: 10px 0;"><h3>${edu.degree || 'Degree'}</h3><p>${edu.school || 'School'}</p></div>`).join('')}</div>` : ''}
        ${resume.projects && resume.projects.length > 0 ? `<div style="margin: 20px 0;"><h2 style="color: #2563eb;">Projects</h2>${resume.projects.map(proj => `<div style="margin: 10px 0;"><h3>${proj.name || 'Project'}</h3><p>${proj.description || 'Description'}</p></div>`).join('')}</div>` : ''}
      </div>
    `;
    tempDiv.style.position = 'absolute';
    tempDiv.style.left = '-9999px';
    document.body.appendChild(tempDiv);

    const opt = {
      margin: 10,
      filename: `${resume.full_name.replace(/\s+/g, '_')}_resume.pdf`,
      image: { type: 'jpeg', quality: 0.98 },
      html2canvas: { scale: 2 },
      jsPDF: { unit: 'mm', format: 'a4', orientation: 'portrait' }
    };

    html2pdf().set(opt).from(tempDiv).save().then(() => {
      document.body.removeChild(tempDiv);
    });
  };

  return (
    <div className="dashboard">
      {/* Backend Status - Add this small indicator */}
      <div style={{
        position: 'absolute',
        top: '10px',
        right: '10px',
        padding: '5px 10px',
        backgroundColor: backendStatus === 'connected' ? '#4CAF50' : '#ff9800',
        color: 'white',
        borderRadius: '15px',
        fontSize: '12px',
        fontWeight: 'bold'
      }}>
        {backendStatus === 'connected' ? 'âœ… Backend' : 'ðŸ”´ Backend'}
      </div>

      {/* YOUR ORIGINAL DASHBOARD CONTENT */}
      <div className="welcome-section">
        <h1>AI Resume Builder</h1>
        <h2>Welcome back, Demo User!</h2>
        <p>Here's an overview of your resumes and documents</p>
      </div>

      <div className="stats-grid">
        <div className="stat-card">
          <h3>{resumesCount}</h3>
          <p>Resumes</p>
        </div>
        <div className="stat-card">
          <h3>2</h3>
          <p>Cover Letters</p>
        </div>
        <div className="stat-card">
          <h3>12</h3>
          <p>Applications Sent</p>
        </div>
        <div className="stat-card">
          <h3>4</h3>
          <p>Interviews</p>
        </div>
      </div>

      <div className="dashboard-nav">
        <button className="nav-btn active">My Resumes</button>
        <button className="nav-btn">Cover Letters</button>
        <button className="nav-btn">Account Settings</button>
      </div>

      <div className="resumes-section">
        <div className="section-header">
          <h2>Your Resumes</h2>
          <button
            onClick={createSampleResume}
            className="create-btn"
          >
            Create New Resume
          </button>
        </div>

        {resumes.length === 0 ? (
          <div className="empty-state">
            <p>No resumes yet. Create your first resume!</p>
          </div>
        ) : (
          <div className="resumes-grid">
            {resumes.map((resume, index) => (
              <div key={index} className="resume-card">
                <h3>{resume.full_name}</h3>
                <p><strong>Email:</strong> {resume.email}</p>
                <p><strong>Phone:</strong> {resume.phone}</p>
                <p><strong>Summary:</strong> {resume.summary}</p>
                <p><strong>Skills:</strong> {resume.skills?.join(', ')}</p>
                <div className="card-actions">
                  <button className="btn-primary" onClick={() => window.location.href = `/resume-editor/${resume.id}`}>Edit</button>
                  <button className="btn-secondary" onClick={() => handleDownloadPDF(resume)}>Download PDF</button>
                  <button className="btn-danger" onClick={() => handleDeleteResume(resume.id)}>Delete</button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
